import React,{Component} from 'react';
import './contact.css';
import 'tachyons';
import FormInput from './FormInput.js';
import CustomButton from './CustomButton.js';
import { FaGithub } from "react-icons/fa";


const initialState = {
	email: '',
	name: '',
  message:''
}

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = initialState;
  }
  handleSubmit = (event) => {
		event.preventDefault();
		this.setState({email: '', password: ''});
	}

	handleChange = (event) => {
		const {value, name} = event.target;
		this.setState({[name]: value});
	}
  render(){

    return (
      <>
      <div className="container">
      <div className='left'>

      <h1 className="mb3">Contact Us</h1>
      <div className="bb bw2 center mt3" style={{width:'35vw'}} ></div>
      <form onSubmit={this.handleSubmit}>
      <FormInput
      label="Name"
      name="name"
      type="text"
      value={this.state.name}
      handleChange={this.handleChange}
      required
      />
      	<FormInput
      	label="Email"
      	name="email"
      	type="email"
      	value={this.state.email}
      	handleChange={this.handleChange}
      	required
      	/>
      	<FormInput
      	label='Message'
      	name="message"
      	type="text"
      	value={this.state.message}
      	handleChange={this.handleChange}
      	required
      	/>
      	<CustomButton type="submit">Contact Us</CustomButton>
      </form>
      </div>
      <div className='right'>
      <h1>Useful Links</h1>
        <div className="bb bw2 center mt3" style={{width:'35vw'}} ></div>
      <div className="links">  <FaGithub size="2em" color="#23d997"/><a href="#"> GitHub</a></div>
        <div  className="links">  <FaGithub size="2em" color="#23d997"/><a href="#"> Email</a></div>
          <div className="links">  <FaGithub size="2em" color="#23d997"/><a href="#"> Instagram</a></div>
            <div className="links">  <FaGithub size="2em" color="#23d997"/><a href="#"> LinkedIn</a></div>
      </div>

      </div>
      <footer className="footer mt6">developed by debugged.exe</footer>
      <footer  className="footer">   software engineers</footer>

</>
    );
  }
}

export default Contact;
