import React,{Component,useState} from 'react';
import './work.css';
import 'tachyons';
import {BsChevronDoubleDown } from "react-icons/bs";
import AOS from 'aos';
import { WorkCompleted } from './workcompleted.js'
import 'bootstrap/dist/css/bootstrap.min.css';
import Carousel from 'react-elastic-carousel'


const Work =()=> {

  const[items,setItems]=useState(WorkCompleted);
  const [visible,setVisible]=useState(3);

   return (
    <div className="home2">
        <div className="mt4">
          <h2 data-aos="fade-up" data-aos-duration="1500"> Projects Completed</h2>
            <div className="bb bw2 left mt4"></div>
              <div className="ma5">
              {
                items.slice(0,2).map((data)=>
                  (
                    <div>
                      <div className="flex justify-around mt5" data-aos="fade-left" data-aos-duration="2000">
                           <Carousel itemsToShow={1}>
                             <img src={data.image1} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                             <img src={data.image2} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                             <img src={data.image3} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                           </Carousel>
                                   <div className="flex flex-column items-center" data-aos="fade-left" data-aos-duration="1000">
                                    <h2> {data.title}</h2>
                                    <p>{data.description}</p>
                                    <a href={data.link}><button className=" book items-center px-4 py-2 mx-auto mt-4 grow br2 mb1" >View webiste</button></a>
                                   </div>
                                 </div>
                      <div className="bt differline mt3 center"></div>
                  </div>
                  )
                )
              }
              <button onClick={showMoreItems}>Show More</button>
            </div>
         </div>
        </div>

      );
    };
export default Work;

