import React,{Component} from 'react';
import './work.css';
import 'tachyons';
import AOS from 'aos';
import 'bootstrap/dist/css/bootstrap.min.css';
import Carousel from 'react-elastic-carousel'
import image1 from '.././testimonials/1.png';
import image2 from '.././testimonials/2.png';
import image3 from '.././testimonials/3.png';
import sharva1 from './sharva1.png';
import sharva2 from './sharva2.png';
import sharva3 from './sharva3.png';



class Work extends Component {
  constructor(props) {
    super(props);
    this.state={
      show:true,
      text:'Show More'
    };

    this.Showdiv=this.Showdiv.bind(this);
  }
  Showdiv=()=>{
    const {show,text}=this.state;
    this.setState({show:!show});
    this.setState({text:'Show less'});
  }
  render(){
   return (
    <div className="home2">
        <div className="mt4">
          <h2 data-aos="fade-up" data-aos-duration="1500"> Projects Completed</h2>
          <div className="bb bw2 left"></div>
            <div className="ma4">

            <div>
              <div className="flex justify-around mt5" data-aos="fade-left" data-aos-duration="2000">
                  <Carousel itemsToShow={1} >
                      <img src={sharva1} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                      <img src={sharva2} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                      <img src={sharva3} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                  </Carousel>
                <div className="flex flex-column items-center" data-aos="fade-left" data-aos-duration="1000">
                 <h2> SHARVA FOUNDATION</h2>
                 <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
                 He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
                 various projects and desire to learn various technologies has always inspired me, as a team member
                 Abhishek earns my highest recommendation.</p>
                 <a href="https://sharvafoundation.org/"><button className=" book items-center px-4 py-2 mx-auto mt-4 grow br2 mb1" >View webiste</button></a>
                </div>
              </div>
          <div className="bt differline mt3 center"></div>
          </div>

          <div>
            <div className="flex justify-around mt5">
                <Carousel itemsToShow={1}>
                    <img src={sharva1} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                    <img src={sharva2} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                    <img src={sharva3} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                </Carousel>
              <div className="flex flex-column items-center">
               <h2> SHARVA FOUNDATION</h2>
               <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
               He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
               various projects and desire to learn various technologies has always inspired me, as a team member
               Abhishek earns my highest recommendation.</p>
               <a href="https://sharvafoundation.org/"><button className=" book items-center px-4 py-2 mx-auto mt-4 grow br2 mb1" >View webiste</button></a>
              </div>
            </div>
        <div className="bt differline mt3 center"></div>
        </div>

        <h3 onClick={this.Showdiv}>{this.state.text}</h3>


            </div>
        </div>
        <div className="mt5 mb5">
            <h2>Upcoming Projects</h2>
            <div className="bb bw2 left"></div>
             <div className="bg-white dib br4 pa3 ma3 grow shadow-5 w-25 ml4 mt4">
             <img src={image3} alt="project sharva" width="270px" height="200px" className="pa2 center"/>
             <p className="p">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse tincidunt, sapien sit amet accumsan aliquam,
             eros ipsum vehicula ligula, in luctus nisi tortor in ligula. </p>
             <a href="https://inmotionhosting.com" ><button className="btn ml5 mb2"  role="button">View Website</button></a>
             </div>

            <div className="bg-white dib br4 pa3 ma3 grow shadow-5 w-25 ml4 mt4 ">
            <img src={image1} alt="project sharva" width="270px" height="200px" className="pa2 center"/>
            <p className="p">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse tincidunt, sapien sit amet accumsan aliquam,
            eros ipsum vehicula ligula, in luctus nisi tortor in ligula. </p>
            <a href="https://inmotionhosting.com" className="btn ml5 mb2"  role="button">View Website</a>
            </div>

            <div className="bg-white dib br4 pa3 ma3 grow shadow-5 w-25 ml4 mt4">
            <img src={image2} alt="project sharva" width="270px" height="200px" className="pa2 center"/>
            <p className="p">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse tincidunt, sapien sit amet accumsan aliquam,
            eros ipsum vehicula ligula, in luctus nisi tortor in ligula. </p>
            <a className="btn ml5 mb2" href="#" role="button">View Website</a>
            </div>
        </div>
    </div>
    );
  }
}

class Show extends Component{
  render()
  {
    return(
      <div>
        <div className="flex justify-around mt5">
            <Carousel itemsToShow={1}>
                <img src={sharva1} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                <img src={sharva2} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                <img src={sharva3} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
            </Carousel>
          <div className="flex flex-column items-center">
           <h2> SHARVA FOUNDATION</h2>
           <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
           He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
           various projects and desire to learn various technologies has always inspired me, as a team member
           Abhishek earns my highest recommendation.</p>
           <a href="https://sharvafoundation.org/"><button className=" book items-center px-4 py-2 mx-auto mt-4 grow br2 mb1" >View webiste</button></a>
          </div>
        </div>
      <div className="bt differline mt3 center"></div>
      </div>

    );
  }
}
export default Work;
