import React,{Component,useState} from 'react';
import './work.css';
import 'tachyons';
import {BsChevronDoubleDown } from "react-icons/bs";
import AOS from 'aos';
import { WorkCompleted } from './workcompleted.js';
import { UpcomingProjects } from './upcomingprojects.js'
import 'bootstrap/dist/css/bootstrap.min.css';
import Carousel from 'react-elastic-carousel'

const Work =()=> {

  AOS.init();
  const[items,setItems]=useState(WorkCompleted);
  const[upcoming,setUpcoming]=useState(UpcomingProjects);
  const [visible,setVisible]=useState(3);

const showMoreItems=()=>
  {
    setVisible((prev)=>prev+3);
  }

   return (
    <div className="home2 ">
        <div className="mt4" id="projcomp">
          <p className="pro ml4 white" data-aos="fade-up" data-aos-duration="1500" style={{fontFamily: 'Yusei Magic'}}> Projects Completed</p>
            <div className="bb bw2 left mt4 "></div>
              <div className="ma5 ">
              {
                items.slice(0,visible).map((data)=>
                  (
                    <div>
                      <div className="projdiv justify-around mt5" data-aos="fade-left" data-aos-duration="2000">
                           <Carousel itemsToShow={1}>
                             <img src={data.image1} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                             <img src={data.image2} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                             <img src={data.image3} alt="project sharva" width="450px" height="260px" className="ml5 mt3"/>
                           </Carousel>
                                   <div className="flex flex-column items-center" data-aos="fade-left" data-aos-duration="1000">
                                    <p  className="pro white" style={{fontFamily: 'Yusei Magic'}}> {data.title}</p>
                                    <p>{data.description}</p>
                                    <a href={data.link}><button className=" book grow br2 mb1" >View webiste</button></a>
                                   </div>
                                 </div>
                      <div className="bt differline mt3 center"></div>
                  </div>
                  )
                )
              }
               <h5 onClick={showMoreItems} className="center showmore mt5">Show More</h5>
               <BsChevronDoubleDown className="arrowdown code" size="2rem"/>

               <div className="mt4">
                 <p className="pro white ml3" data-aos="fade-up" data-aos-duration="1500" style={{fontFamily: 'Yusei Magic'}}> Upcoming Completed</p>
                 <div className="bb bw2 left mt4"></div>
                 <Carousel className="mt5 center" style={{width:'90%',height:'80%'}}>
                 {
                   upcoming.map((project)=>
                   (
                     <div className="proj br bl bw1 code">
                       <h2 className="mt5 white fw5" style={{fontFamily: 'Yusei Magic'}}>{project.title.toUpperCase()}</h2>
                       <div className="bb center mt4 upcomingline"></div>
                       <p className="pa4">{project.description}</p>
                    </div>
                   ))
                 }
                </Carousel>
               </div>
           </div>
         </div>

             <footer className="footer mt6">developed by debugged.exe</footer>
             <footer  className="footer">websites | mobile applications | digital marketing</footer>

        </div>
      );
    };
export default Work;
