import React,{Component} from 'react';
import  { useState } from 'react';
import FAQ from './FAQ';
import './home.css';
import { BiMobile ,BiLaptop} from "react-icons/bi";
import logo from './logo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Carousel} from 'react-bootstrap';
import image1 from '././testimonials/1.png';
import image2 from '././testimonials/2.png';
import image3 from '././testimonials/3.png';

class Home extends Component {
  constructor() {
    super();}
  render(){
    const [faqs, setfaqs] = useState([
      {
        question: 'How many programmers does it take to screw in a lightbulb?',
        answer: 'None. We don\'t address hardware issues.',
        open: true
      },
      {
        question: 'Who is the most awesome person?',
        answer: 'You. The Viewer.',
        open: false
      },
      {
        question: 'How many questions does it take to make a successful FAQ Page?',
        answer: 'This many.',
        open: false
      }
    ]);

    const toggleFAQ = index => {
      setfaqs(faqs.map((faq, i) => {
        if (i === index) {
          faq.open = !faq.open
        } else {
          faq.open = false;
        }

        return faq;
      }))
    }

    return (
      <div className="Home"  style={{backgroundcolor:'black'}}>

      <div className="pa5">
       &nbsp;
      <h1>Think twice </h1>
      <h1><span className="code" >code</span> once</h1>
      <p >
         I am a software engineer, a passionate coding enthusiast, in love with
    solving real world problems with the power of coding since i was 13. I am
    highly organized, I take my time to analyze before I move forward and I
    aspire to utilize my skills in an organization which gives me an opportunity
                    to learn and grow.
      </p>
      &nbsp;
      &nbsp;
      </div>


             <div>
             <img src={logo} alt="debugged icon" className="logo grow "/>
             </div>
             &nbsp;
             &nbsp;



    <div>
      <h1>Our <span className="code">services</span>.</h1>
      <div className="services">
        <div ><BiMobile size="3em" color="#23d997"/>Android/iOS Development
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Web Development
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Wireframing
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Server/Domain hosting
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Social Media handling
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Web Security
        <hr className="underline"/>
        </div>
        <div ><BiLaptop size="3em" color="#23d997"/> Digital marketing
        <hr className="underline"/>
        </div>
      </div>
      </div>
      &nbsp;
      <div>
        <h1>Our <span className="code">testimonials</span>.</h1>
        &nbsp;
        &nbsp;
        &nbsp;
        <Carousel >

        <Carousel.Item interval={1500}>
          <img
            className="d-block w-100"
            src={image1}
            alt="First slide"
            className="test"
          />
          &nbsp;
          &nbsp;
          <h3>Tanmay Jagtap</h3>
          &nbsp;
          <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
          He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
          various projects and desire to learn various technologies has always inspired me, as a team member
          Abhishek earns my highest recommendation.</p>
        <p>  5th January 2021</p>
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
        </Carousel.Item>


        <Carousel.Item interval={1500}>
          <img
            className="d-block w-100"
            src={image2}
            alt="Second slide"
            className="test"
          />
          &nbsp;
          <h3>Tejas Manchakar</h3>
          &nbsp;
          <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
          He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
          various projects and desire to learn various technologies has always inspired me, as a team member
          Abhishek earns my highest recommendation.</p>

          <p>5th January 2021</p>
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src={image3}
            alt="Third slide"
            className="test"
          />&nbsp;
          <h3>Rehan Shaikh</h3>
          &nbsp;
          <p>We have been hands on various projects together and Abhishek is one of the best people I had as a patner,
          He has strong problem solving skills and his ability for tackling problems is remarkable! His dedication to work on
           various projects and desire to learn various technologies has always inspired me, as a team member
           Abhishek earns my highest recommendation.</p>

        <p>  5th January 2021</p>
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;
          &nbsp;

        </Carousel.Item>
      </Carousel>
      </div>


      &nbsp;
      &nbsp;
      &nbsp;
      <div class="ques">
       <h1>Any Questions</h1>
       <h1><span className="code">FAQ</span></h1>
       &nbsp;
       &nbsp;
       <p></p>

       <div className="f4">
      <div className="myDIV">What kind of projects do we want to work on?</div>
         <div className="hide">we want to work on scalable projects which would give us an
          opportunity to enhance our core skills and exhibite the best of our abilities</div>
         &nbsp;
